import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import * as cal from './Heading.jsx';
import './index.css'
import reactDom from 'react-dom';

function App() {
  var time =  new Date().getHours();
  var greeting;
  var [numberz, setNumber] = useState("")
  var [buttonz, setButtonz] = useState("")
  var [please, setPlease] = useState("please enter your name")
  
  const [entername, setName] = useState({
      fName : "",
      lName : ""
    });

  const [newPerson, setNewPerson] = useState({
      ID: "",
      Ten: "",
      SDT: "",
      Email: ""
    });
    //style của dòng chữ
    var sitai = {
    textAlign: "center",
    color: "yellow",
    fontSize: "20px",
    marginTop: "15%"
    }
    //set up thời gian 
    if (time >= 0 && time < 12){
      greeting = "Good morning, ";
      sitai.color = "red";
    } else if (time >= 12 && time < 18){
      greeting = "Good afternoon, ";
      sitai.color = "blue";
    } else {
      greeting = "Good evening, ";
      sitai.color = "green";
    }
    //hàm khi nhập vào ô họ tên
    function handleChange(event) {
      const {name, value} = event.target;
      setName(prevValue => {
       return {
         ...prevValue,
         [name] : value
      };
    });
      if(entername.fName === "" && value === "" || entername.lName === "" && value === "" ) {
        setPlease("please enter your name");
        setButtonz("")
      }
      else { setPlease("Mr"); setButtonz(<button id = "ba" type = "button" onClick = {Mess}>Click me</button>)}
    }
    
    //hàm khi nhập vào ô thêm object
    function addingPerson (event) {
      setNewPerson(prevValue => {
        const {name, value} = event.target;
        return {
          ...prevValue,
          [name] : value
        }
      })
    }
    //hàm khi nhấn nút ADD
    function addButtonClicked () {
      ReactDOM.render(
      newPerson.map( newPerson =>
        <cal.default 
        id = {newPerson.ID}
        name = {newPerson.Ten}
        tel = {newPerson.SDT}
        email = {newPerson.email}
        />
      ),
      document.getElementById('Person')
      )
    }


    return (
    <div>
      <h1 style = {sitai}>
        {greeting} {please} {entername.fName} {entername.lName} {numberz}
      </h1>
      <form>
      <input className = "inpus" id = "mot" onChange = {handleChange} name = "fName" placeholder = "YourName" value = {entername.fName}/>
      <input className = "inpus" id = "hai" onChange = {handleChange} name = "lName" placeholder = "Surname" value = {entername.lName}/>
      {buttonz}
      </form>
     </div> )
      
      function Mess(e) {
        setInterval ( () => {setNumber(numberz = ", it's " + new Date())});
        document.getElementById('mot').style.display = "none";
        document.getElementById('hai').style.display = "none";
        document.getElementById('ba').style.display = "none";
        ReactDOM.render(
         <div>
           <form>
             <input id = "NhapID" onChange = {addingPerson} placeholder = "ID" name = "ID" value = {newPerson.ID} />
             <input id = "NhapTen" onChange = {addingPerson} placeholder = "Ten" name = "Ten" value = {newPerson.Ten} />
             <input id = "NhapSDT" onChange = {addingPerson} placeholder = "SDT" name = "SDT" value = {newPerson.SDT} />
             <input id = "NhapEmail" onChange = {addingPerson} placeholder = "Email" name ="Email" value = {newPerson.Email} />
             <button type = "button" onChange = {addingPerson} id = "NutThemNguoi">ADD</button>
           </form>
         </div>,
        document.getElementById('nect')
        );
        //e.preventDefault()
      }
}
export default App;
