import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

var name = "Mẫn"
var time =  new Date().getHours()

ReactDOM.render(
  <div>
    <h1 id="gud"></h1>
   if ({time >= 0 } && {time <= 12}  ) {
     document.getElementById("gud").innerHTML = "good morning"
   }
   if ({time > 12} && {time <= 18}  ) {
     document.getElementById("gud").innerHTML = "good afternoon"
   }
   else if ({time > 18} && {time <= 23}  ) {
     document.getElementById("gud").innerHTML = "good evening"
   }
  </div>,
  document.getElementById('roots')
);

