import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import Xam from './TaoTrangCua1GameCuThe'
import Taothe from './TaoDanhSachGame'
import Hotgame, {Hotgamesnhi, Gameduoctim} from './hotgames';
import {  BrowserRouter as Router,Route, Routes, Link, Navigate} from 'react-router-dom'
import contact from './contacts';
import './lalala.css'
import './grid.css'
import './repponsive.css'
import './style.css'
import './style2.css'

var ccc = window.location.pathname

function SearchGame(){
	var [timgame, setTimgame] = useState("")
  function CreateCard(contact) {
	if(contact.name == timgame)
	  return (
		<cal.default 
		id = {contact.id}
		image = {contact.image}
		name = {contact.name}
		ngayph = {contact.ngayph.toString()}
		theloai = {contact.theloai}
		solike = {contact.solike}
		sodislike = {contact.sodislike}
		socmt = {contact.socmt}
		 />
	  )
  }
  function Doichu(event) {
	  const ttt = event.target.value
	  setTimgame(ttt)
  }
  function Taothez() {
	  return (
		<div>
		  <input name = "tengame" onChange={Doichu}></input>
		  <div className = "cacgame">
		  {contact.map(CreateCard)}
		  </div>
		</div>
	  )
	}
	return Taothez()
  }

function Home() {
	const [hotornew, setBien] = useState(0)
	if (hotornew == 0) contact.sort(function(a, b){return (b.solike + b.sodislike) - (a.solike + a.sodislike)})
	else contact.sort(function(a, b){return b.ngayph - a.ngayph})
	return (
		<div class="table">
		<SearchGame/>
  		<img src="/img/games-header-1.png" id="top-header" />
			<div class="body-content"> 
					<button class="content-btn" onClick={() => setBien(0)}>Game Hot</button>
					<button class="content-btn" onClick={() => setBien(1)}>Game mới</button>
				<div className = "mucgamehot">
					<Hotgame />
					<Hotgamesnhi />	
				</div>
			</div>
			<footer class="footer">
				<div>
					<a href="home.html"><img src="/img/logo.png"/></a>
				<div id="copyright">
				&copy; 2021
				</div>
					<p id="web-decrip">Website chia sẻ thông tin về những trò chơi dược yêu thích</p>
				</div>
			</footer>
	</div>
  );
}

function App() {

  return (
    <div>
    <Router>
	<div>
        <header className="headerr">
		<nav className="navibar">
		<a href="home.html"><img src="/img/WebLogo1.jpg"  class="logo"/></a>
		<div class="dropdowns">	
		<button class="btns">Thể loại</button>
		<div class="dropdowns-content">
    		<Link to="/Kinhdi" onClick = {window.location.reload} class="dropdowns-content-tab">Kinh dị</Link>
			<Link to="/Giaido" onClick = {window.location.reload} class="dropdowns-content-tab">Giải đố</Link>
			<Link to="/Thethao" onClick = {window.location.reload} class="dropdowns-content-tab">Thể thao</Link>
		</div>
		</div>
		<a href="file:///C|/Users/HAU PC/OneDrive/Desktop/nộp bài/internet và công nghệ web/Báo cáo/gioithieu.html"><button class="btns">giới thiệu</button></a>
		<a href="file:///C|/Users/HAU PC/OneDrive/Desktop/nộp bài/internet và công nghệ web/Báo cáo/dangnhap.html" ><button class="btns">đăng nhập</button></a>
		<a href="file:///C|/Users/HAU PC/OneDrive/Desktop/nộp bài/internet và công nghệ web/Báo cáo/dangki.html"><button class="btns">đăng kí</button></a>
		<a href="file:///C|/Users/HAU PC/OneDrive/Desktop/nộp bài/internet và công nghệ web/Báo cáo/gopy.html"><button class="btns">góp ý</button></a>
		</nav>	
	</header>
      </div>
      <Routes>
        <Route exact path = "/" element = {<Home/>} />
       <Route  path = "/Kinhdi" element = {<Taothe/>}/>
	   <Route  path = "/Giaido" element = {<Taothe/>}/>
	   <Route  path = "/Thethao" element = {<Taothe/>}/>
       <Route  path = {ccc} element = {<Xam/>} />
      </Routes>    
  </Router>
  </div>
  )
 }

export default App;
