import React, {useState} from 'react';
import * as cal from './Heading.jsx';
import contact from './contacts.js'
import './index.css'

function App() {
  var time =  new Date().getHours();
    var greeting;
    var [numberz, setNumber] = useState(new Date())
    var [entername, setName] = useState({
      Name : "please enter your name",
      SurName: ""
    });
    
    var sitai = {
    color: "yellow",
    fontSize: "20px",
    }
    setInterval ( () => {setNumber(numberz = new Date())});
    if (time >= 0 && time < 12){
        greeting = "Good morning, " + entername.Name + entername.SurName;
        sitai.color = "red";
    } else if (time >= 12 && time < 18){
        greeting = "Good afternoon, " + entername.Name + entername.SurName;
        sitai.color = "blue";
    } else {
        greeting = "Good evening, " + entername.Name + entername.SurName;
        sitai.color = "green";
    }
    return (
    <div>
      <h1 style = {sitai}>{greeting}</h1>
      <input placeholder = "Name" value = {entername.Name}/>
      <input placeholder = "Surname" value = {entername.SurName}/>
     </div> )
    function Welcome(){
    return contact.map( contact => 
      <cal.default 
      key = {contact.id}
      id = {contact.id}
      name = {contact.name}
      tel = {contact.tel}
      email = {contact.email}
      />)
    }
}
export default App;
