import React, {useState} from 'react';
import ReactDOM from 'react-dom';
import * as cal from './Heading.jsx';
import './index.css'
import reactDom from 'react-dom';
import contact from './contacts'

function App() {
  var time =  new Date().getHours();
  var greeting;
  var [numberz, setNumber] = useState("")
  var [buttonz, setButtonz] = useState("")
  var [please, setPlease] = useState("please enter your name")
  
  const [entername, setName] = useState({
      fName : "",
      lName : ""
    });
const [inputText, setInputText] = useState({
  id: "",
  name: "",
  tel: "",
  email: ""
});
const [items, setItems] = useState([]);
    //style của dòng chữ
    var sitai = {
    textAlign: "center",
    color: "yellow",
    fontSize: "20px",
    marginTop: "15%"
    }
  //set up thời gian 
    if (time >= 0 && time < 12){
      greeting = "Good morning, ";
      sitai.color = "red";
    } else if (time >= 12 && time < 18){
      greeting = "Good afternoon, ";
      sitai.color = "blue";
    } else {
      greeting = "Good evening, ";
      sitai.color = "green";
    }
  //hàm khi nhập vào ô họ tên
    function handleChange(event) {
      const {name, value} = event.target;
      setName(prevValue => {
       return {
         ...prevValue,
         [name] : value
      };
    });
      if(entername.fName === "" && value === "" || entername.lName === "" && value === "" ) {
        setPlease("please enter your name");
        setButtonz("")
      }
      else { setPlease("Mr"); setButtonz(<button id = "ba" type = "button" onClick = {Mess}>Click me</button>)}
    }
  
  //hàm khi nhập vào ô thêm object
  function addingPerson (event) {
    const {value, name} = event.target
    setInputText(prevValue => {
      return {
        ...prevValue,
        [name]: value
      }
    })
  }
  //hàm khi nhấn nút ADD
  function addButtonClicked () {
    setItems(prevItems => {
      return [...prevItems, inputText]
    })
    };
    
  return (
    <div>
      <h1 style = {sitai}>
        {greeting} {please} {entername.fName} {entername.lName} {numberz}
      </h1>
      <form>
        <input id = "mot" onChange = {handleChange} name = "fName" placeholder = "YourName" />
        <input id = "hai" onChange = {handleChange} name = "lName" placeholder = "Surname" />
        {buttonz}
      </form>
     </div> )
    
      function Mess() {
        setInterval ( () => {setNumber(numberz = ", it's " + new Date())});
        document.getElementById('mot').style.display = "none";
        document.getElementById('hai').style.display = "none";
        document.getElementById('ba').style.display = "none";
        ReactDOM.render(
       <div>
         <form>
           <input id = "NhapID" onChange = {addingPerson} placeholder = "Nhập id" type = "text" name = "id"  value = {items.id} />
           <input id = "NhapTen" onChange = {addingPerson} placeholder = "Nhập tên" type = "text" name = "name"  value = {items.name}/>
           <input id = "NhapSDT" onChange = {addingPerson} placeholder = "Nhập sđt" type = "text" name = "tel" value = {items.tel}/>
           <input id = "NhapEmail" onChange = {addingPerson} placeholder = "Nhập email" type = "text" name = "email" value = {items.email}/>          
           <button type = "button" onClick = {addButtonClicked} id = "NutThemNguoi">ADD</button>
           <div>
             {contact.map(contact => {
               return (
               <cal.default 
               id = {contact.id}
               name = {contact.name}
               tel = {contact.tel}
               email = {contact.email} />
               )
            })}
            </div>           
         </form>
       </div>,
        document.getElementById('nect')
        );
      //e.preventDefault()
      }
    
}
export default App;
