import React, {useState} from 'react';
import * as cal from './Heading'

function ThePeople () {
    const [inputText, setInputText] = useState({
        id: " ",
        name: " ",
        tel: " ",
        email: " "
      });
    
    const [items, setItems] = useState([]);

    function addingPerson (event) {
        const {value, name} = event.target
        setInputText(prevValue => {
          return {
            ...prevValue,
            [name]: value
          }
        })
      }
      //hàm khi nhấn nút ADD
    function addButtonClicked () {
    setItems(prevItems => {
        return [...prevItems, inputText]
    })
    };
    return (
        <div>
            <form>
                <input id = "NhapID" onChange = {addingPerson} placeholder = "Nhập id" type = "text" name = "id"  value = {items.id} />
                <input id = "NhapTen" onChange = {addingPerson} placeholder = "Nhập tên" type = "text" name = "name"  value = {items.name}/>
                <input id = "NhapSDT" onChange = {addingPerson} placeholder = "Nhập sđt" type = "text" name = "tel" value = {items.tel}/>
                <input id = "NhapEmail" onChange = {addingPerson} placeholder = "Nhập email" type = "text" name = "email" value = {items.email}/>          
                <button type = "button" onClick = {addButtonClicked} id = "NutThemNguoi">ADD</button>
                <div>
                  {items.map(heading => {
                    return (
                    <cal.default 
                    id = {heading.id}
                    name = {heading.name}
                    tel = {heading.tel}
                    email = {heading.email} />
                    )
                 })}
                 </div>           
                </form>
        </div>
        )
}
export default ThePeople
