import React, {useState} from 'react';
import Hot, {HotNhi} from './TrangHotGames';
import contact from './contacts'

var nhat = 1;
function createCard(contact) {
    if (nhat == 1) {
    nhat--;
    return (
      <Hot
      id = {contact.id}
      image = {contact.image}
      name = {contact.name}
      ngayph = {contact.ngayph}
      theloai = {contact.theloai}
      solike = {contact.solike}
      sodislike = {contact.sodislike}
      socmt = {contact.socmt}
      thumbnail = {contact.thumbnail}
       />
    )
    }
}
function createCardz(contact) {
  if (nhat > -3) {
    if (nhat == 1) {nhat--; return 1}
    else{
    nhat--;
    return (
    <HotNhi 
      id = {contact.id}
      image = {contact.image}
      name = {contact.name}
      ngayph = {contact.ngayph}
      theloai = {contact.theloai}
      solike = {contact.solike}
      sodislike = {contact.sodislike}
      socmt = {contact.socmt}
    />
    )
    }
    }
}
  function Hotgame() {
    contact.sort(function(a, b){return (b.solike + b.sodislike) - (a.solike + a.sodislike)});
    return (
        <div className = "cacgamehot">
        {contact.map(createCard)}
        </div>
    )
  }
  function Hotgamesnhi() {
    contact.sort(function(a, b){return (b.solike + b.sodislike) - (a.solike + a.sodislike)});
    return (
        <div className = "cacgamehotnhi">
        {contact.map(createCardz)}
        </div>
    )
  }
  export default Hotgame;
  export {Hotgamesnhi}
