import React, {useState} from 'react';
import Hot, {HotNhi} from './TrangHotGames';
import contact from './contacts'

function createCardz(contact) {
  return (
    <HotNhi 
    id = {contact.id}
    image = {contact.image}
    name = {contact.name}
    ngayph = {contact.ngayph}
    theloai = {contact.theloai}
    solike = {contact.solike}
    sodislike = {contact.sodislike}
    socmt = {contact.socmt}
    />
    )
  }
  function Hotgame(){
  var [timgame, setTimgame] = useState("")
  if(timgame == "") contact = contact.slice(0,1)
  function createCard(contact) {
      return (
        <Hot
        id = {contact.id}
        image = {contact.image}
        name = {contact.name}
        ngayph = {contact.ngayph}
        theloai = {contact.theloai}
        solike = {contact.solike}
        sodislike = {contact.sodislike}
        socmt = {contact.socmt}
        thumbnail = {contact.thumbnail}
         />
      )
  }
function Hotgamez() {
      function Doichu(event) {
        const ttt = event.target.value
        setTimgame(ttt)
      }
    return (
      <div>
        <input onChange={Doichu}></input>
        <div className = "cacgamehot">
        {contact.map(createCard)}
        </div>
      </div>
    )
  }
  return Hotgamez()
}
  function Hotgamesnhi() {
    
    return (
        <div className = "cacgamehotnhi">
        {contact.slice(1,4).map(createCardz)}
        </div>
    )
  }
  export default Hotgame;
  export {Hotgamesnhi}
