import React, {useState} from 'react';
import * as cal from './Heading.jsx';
import contact from './contacts.js'
import './index.css'

function App() {
  var time =  new Date().getHours();
    var greeting;
    var [please, setPlease] = useState("please enter your name")
    var [numberz, setNumber] = useState(new Date())
    const [entername, setName] = useState({
      fName : "",
      lName : ""
    });
    
    var sitai = {
    color: "yellow",
    fontSize: "20px",
    }
    setInterval ( () => {setNumber(numberz = new Date())});
    if (time >= 0 && time < 12){
      greeting = "Good morning, ";
      sitai.color = "red";
    } else if (time >= 12 && time < 18){
      greeting = "Good afternoon, ";
      sitai.color = "blue";
    } else {
      greeting = "Good evening, ";
      sitai.color = "green";
    }
    function handleChange(event) {
      const newValue = event.target.value;
      const inutName = event.target.name;
      setName((prevValue) => {
        if(inutName === "fName") {
          return {
            fName: newValue,
            lName: prevValue.lName
          };
        }
        else if(inutName === "lName") {
          return {
            fName: prevValue.fName,
            lName: newValue
          };
        }
      }
      );
      if(entername.fName === "" && newValue === "" || entername.lName === "" && newValue === "" ) setPlease("please enter your name")
      else setPlease("Mr")
      event.preventDefault();
    }
    return (
    <div>
      <h1 style = {sitai}>
        {greeting} {please} {entername.fName} {entername.lName}
      </h1>
      <form onSubmit = {handleChange}>
      <input onChange = {handleChange} name = "fName" placeholder = "YourName" value = {entername.fName}/>
      <input onChange = {handleChange} name = "lName" placeholder = "Surname" value = {entername.lName}/>
      <button type = "submit">Submit</button>
      </form>
     </div> )
    function welcome(event) {
     return contact.map( contact => 
      <cal.default 
      key = {contact.id}
      id = {contact.id}
      name = {contact.name}
      tel = {contact.tel}
      email = {contact.email}
      />)
    }
}
export default App;
