import Hot from './TrangHotGames';
import contact from './contacts'

var nhat = 1;
function createCard(contact) {
    if (nhat == 1) {
    nhat--;
    return (
      <Hot
      id = {contact.id}
      image = {contact.image}
      name = {contact.name}
      ngayph = {contact.ngayph}
      theloai = {contact.theloai}
      solike = {contact.solike}
      sodislike = {contact.sodislike}
      socmt = {contact.socmt}
      thumbnail = {contact.thumbnail}
       />
    )
    }
}
  function Hotgames() {
    contact.sort(function(a, b){return (a.solike + sodislike) - (b.sodislike + b.sodislike)});
    return (
        <div className = "cacgamehot">
        {contact.map(createCard)}
        </div>
    )
  }
